con:config.script:
// Setup and TearDown at TestSuite Level.
testRunner.testCase.testSuite.project.getTestSuiteByName('Test_Suite_1').setSetupScript('log.info "This is Setup Script for TestSuite 1."');
testRunner.testCase.testSuite.project.getTestSuiteByName('Test_Suite_1').setTearDownScript('log.info "This is TearDown Script for TestSuite 1."');
log.info ("Setup Script of TestSuite : " + testRunner.testCase.testSuite.getSetupScript()); // Get Setup Script
log.info ("TearDown Script of TestSuite : " + testRunner.testCase.testSuite.getTearDownScript()); // Get TearDown Script

// Setup and TearDown at TestCase Level.
testRunner.testCase.testSuite.project.getTestSuiteByName('Test_Suite_1').getTestCaseByName('TestCase 1').setSetupScript('log.info "This is Setup Script for TestCase 1."');
testRunner.testCase.testSuite.project.getTestSuiteByName('Test_Suite_1').getTestCaseByName('TestCase 1').setTearDownScript('log.info "This is TearDown Script for TestCase 1."');
log.info ("Setup Script of TestCase : " + testRunner.testCase.getSetupScript());  // Get Setup Script
log.info ("TearDown Script of TestCase : " + testRunner.testCase.getTearDownScript());  // Get TearDown Script

con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder;

// 1. Check Response Time taken.
log.info(" Response Time taken = " + messageExchange.getTimeTaken());
assert messageExchange.timeTaken < 7000;

// 2. Check for Endpoint.
 log.info(" Endpoint = " + messageExchange.getEndpoint()); 
 def endpointStr = "http://www.oorsprong.org/websamples.countryinfo/CountryInfoService.wso";
 assert messageExchange.getEndpoint() == endpointStr;

// 3. Check for Response Headers.
log.info(" Header 1 = " + messageExchange.responseHeaders["Content-Length"]);
assert messageExchange.responseHeaders["Content-Length"] != null;
log.info(" Header 2 = " + messageExchange.responseHeaders["Content-Encoding"]);
assert messageExchange.responseHeaders["Content-Encoding"] != null;
log.info(" Header 3 = " + messageExchange.responseHeaders["#status#"]);
def status = messageExchange.responseHeaders["#status#"];
assert status.toString().equals('[HTTP/1.1 200 OK]');

// 4. Check for Attachments in a Response.
log.info(" Attachments = " + messageExchange.responseAttachments.length);
assert messageExchange.responseAttachments.length == 0;

// 5. Validate the existence of a string in the SOAP response.
def holder = new XmlHolder(messageExchange.responseContent); // Holds response message.
def responseXml = holder.getPrettyXml(); // Converts XML response to the string.
assert responseXml.contains("Rupees");

// 6. Validate a string is not present in the SOAP response.
def holder1 = new XmlHolder(messageExchange.responseContent);
def responseXml1 = holder.getPrettyXml();
assert !responseXml1.contains("Test123");

// 7. Validate the value of a node in the SOAP response.
def holder2 = new XmlHolder(messageExchange.responseContent);
def responseXml2 = holder.getPrettyXml();
def currencyName = holder2.getNodeValue("//m:CountryCurrencyResult/m:sName");
assert currencyName.equals("Rupees");

// Alternative Way - Validate the value of a node in the SOAP response.
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
// def requsetHolder = groovyUtils.getXmlHolder(messageExchange.requestContent); // Holds request message.
def responseHolder = groovyUtils.getXmlHolder(messageExchange.responseContent); // Holds response message.
def currency = responseHolder.getNodeValue("//m:CountryCurrencyResult/m:sName");
assert currency == "Rupees";

// 8. To get Response as XML String.
def response = messageExchange.responseContent.toString();
log.info(" Response = " + response);

// Get JSON Response
import groovy.json.JsonSlurper;
def responseMessage = messageExchange.responseContent;
def json = new JsonSlurper().parseText(responseMessage);
// Now, json variable holds entire response. So, we can access any of it's node directly like name, capital etc.

// 1. Assert node values
log.info(" Name = " + json.name);
log.info(" Capital = " + json.capital);
assert json.capital.toString() != null;
assert json.name.toString() == "[British Indian Ocean Territory, India]";

// To Get TestStep Name.
def testStepName = messageExchange.modelItem.testStep.name;
log.info(" Test Step Name = " + testStepName);

// 6. To get Response as XML String.
def response = messageExchange.responseContentAsXml.toString(); 
log.info(" Response = " + response);

con:soapui-project.con:afterLoadScript:
def setupFile = new File("E:/My Work/Testing and Database/API Testing/Manual/From Step By Step/Setup.txt").text;
def tearDownFile  = new File("E:/My Work/Testing and Database/API Testing/Manual/From Step By Step/TearDown.txt").text;

project.getTestSuiteList().each { testSuite ->
	testSuite.getTestCaseList().each { testCase ->
  		testCase.setSetupScript(setupFile);
  		testCase.setTearDownScript(tearDownFile);
 	}
}
